class Solution:
    def minimumCardPickup(self, cards: List[int]) -> int:
        card1Pos = {}
        card2Pos = {}
        minCards = float('Inf')
        for i in range(len(cards)):
            if cards[i] in card1Pos:
                if cards[i] in card2Pos:
                    card1Pos[cards[i]] = card2Pos[cards[i]]
                card2Pos[cards[i]] = i
                consecCards = card2Pos[cards[i]] - card1Pos[cards[i]] + 1
                minCards = min(minCards, consecCards)
                if minCards == 2:
                    return minCards
            else:
                card1Pos[cards[i]] = i
        return -1 if minCards == float('Inf') else minCards


