class Solution {
public:
    int minimumCardPickup(vector<int>& a) {
        unordered_map< int,int> m;
        int ans=INT_MAX,j=0;
        for( int i=0;i<a.size();i++){

            m[a[i]]++;
            while(  j<i && m[a[i]] >1){
                if( a[i]==a[j]){
                    m[a[i]]--;
                    ans=min( ans, i-j+1);
                }
                else m.erase(a[j]);
                j++;
            }
        }
        return ans!=INT_MAX ? ans :-1;
    }
};
