// min returns minimum of two integers.
func min(x, y int) int {
    if x < y {
        return x
    }
    return y
}

func minimumCardPickup(cards []int) int {
	// Keep track of last index of the same value.
    firstIdx := make(map[int]int)

	distance := math.MaxInt
    for i, card := range cards {
        prevIdx, ok := firstIdx[card]
        if ok {
			// Calculate distance between current card and when we saw it last.
            distance = min(distance, i - prevIdx + 1)
        }
		// Always update the indices for cases where we might see duplicates.
        firstIdx[card] = i
    }
    if distance == math.MaxInt {
        return -1
    }
    return distance
}
