class Solution {
    public int minimumCardPickup(int[] cards) {
        // Map or array to store the last index of the number
        int index[] = new int[1000001];
        //Pre-fill to check if values for the card is present while iterating
        Arrays.fill(index,-1);
        int ans = Integer.MAX_VALUE;
        for(int i=0;i<cards.length;i++){
            //Getting minimum distance between last filled and the current in case of reoccurence
            if(index[cards[i]]!=-1) ans = Math.min(ans,i-index[cards[i]]);
            index[cards[i]] = i;
        }
        // Add 1 to count the present card as well
        return ans==Integer.MAX_VALUE?-1:ans+1;
    }
}
