			while (hmap[cards[right]] >= 2) {
				if ((cards[left] != cards[right]) 
					|| hmap[cards[left]] > 2) hmap[cards[left++]]--;
				else break;
			}
			res = min(res, right - left + 1);
		}
	}
	return res == INT_MAX ? -1 : res;
}
