class Solution {
    public int minimumCardPickup(int[] a) 
    {

        int n=a.length;

        //Range our our ans will be between 2 to number of elements in the array
        int start=2; 
        int end=n;

        int ans=-1;

        //Binary search on ans
        while(start<=end)
        {
            //lets assume we need to pick mid num of cards to get ATLEAST two cards with same number
            int mid=start+(end-start)/2;


            //if it is possible to do the above task then our assumed min might be the ans or maybe there is some number               lesser than mid
            if(isPossible(a,mid,n))
            {
                ans=mid;
                end=mid-1;
            }

            else
            start=mid+1;
        }

        return ans;
    }

    public boolean isPossible(int a[],int size,int n)
    {
        Set<Integer> set=new HashSet<>();

        for(int i=0;i<size;i++)
        {
            if(set.contains(a[i]))
            return true;

            set.add(a[i]);
        }

        int j=0;
        for(int i=size;i<n;i++)
        {
            set.remove(a[j]);

            if(set.contains(a[i]))
            return true;

            set.add(a[i]);
            j++;
        }

        return false;
    }
}
