int minimumCardPickup(vector<int>& c) {

        auto canbe = [&](int x) -> bool {
            const int siz =1e6 + 5 ; 
            int fq[siz] = {0} ; 
            for(int i = 0 ; i<sz(c) ; i++){
                fq[c[i]]++ ;
                if(i >= x) fq[c[i - x]]-- ;
                if(fq[c[i]] == 2) return true ;
            }
            return false ; 
        } ; 

        int ans = -1; 
        int l = 2 , h = sz(c) ; 
        while(l <= h){
            int mid = (l + h)/2 ; 
            if(canbe(mid)){
                ans = mid ; 
                h = mid - 1 ; 
            }else l = mid + 1 ; 
        }
        return ans ; 
    }
