class Solution {
public:
    int minimumCardPickup(vector<int>& cards) {
        int n = cards.size();
        unordered_map<int,int> look;

        int i=0, minimum=INT_MAX,current=0;

        for(i=0;i<n;i++)
        {
            if(look.find(cards[i]) == look.end())
                look[cards[i]] = i;
            else{
                current = i-look[cards[i]]+1;
                if(current < minimum)
                    minimum=current;
                look[cards[i]] = i;
            }
        }

        if(minimum == INT_MAX)
            return -1;


        return minimum;
    }
};
