    int minimumCardPickup(vector<int>& cards) {
        unordered_set<int>st;
        int ans=INT_MAX;
        int i=0,j=0;
        while(i<cards.size()){
            if(st.find(cards[i])==st.end()){
                st.insert(cards[i]);
                i++;
            }else{
                ans=min(ans,i-j+1);
                while(j<=i && st.find(cards[i])!=st.end()){
