    for (let end = 0; end < cards.length; end++) {
        while (setOfNumber.has(cards[end])) {
            const size = (end - start) + 1;
            minLength = Math.min(minLength, size);
            setOfNumber.delete(cards[start]);
            start += 1;
        }

        setOfNumber.add(cards[end]);
    }

    return minLength !== Infinity ? minLength : -1;
};
