/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */

// Applicable to all Trees
class Solution 
{
    int num = 0;

    public boolean checkTree(TreeNode root) 
    {
        inorder(root);

        //subtract the root val and cmp them
        if((num - root.val) == root.val)
            return true;

        return false;
    }

    //recursive function
    public void inorder(TreeNode node)
    {
        //if reach the end
        if (node == null)
            return;

        //recuring on the left child
        inorder(node.left);

        //add all the values
        num += node.val;

        //then recuring on the right child
        inorder(node.right);
    }
}
```

