class Solution {
public:
    vector<string> removeAnagrams(vector<string>& words) {
        for(int i = 1; i < words.size(); i++){ //start the loop from first index
            string a = words[i-1]; 
            map <char, int> mp; //stores the details of the (i-1)st word
            for(int j = 0; j < a.length(); j++){
                mp[words[i-1][j]]++;
            }
            map <char, int> m; //stores the details of the i th word
            for(int h = 0; h < words[i].length(); h++){
                m[words[i][h]]++;
            }
            if(mp == m){ //if maps are equal then the words are anagrams
                words.erase(words.begin() + i);
                i--; //as we delete from the vector, we need to reset i to i-1
            }
        }
        return words;
    }
};
