char ** removeAnagrams(char ** words, int wordsSize, int* returnSize){
    (*returnSize) = 1;

    int len;
    int i;
    for (i=1; i<wordsSize; ++i)
    {
        if (isAnagrams(words[(*returnSize)-1], words[i]) == true)
        {
            continue;
