        for(int i=0;i<Math.max(s.length(),t.length());i++)
        {
            if(i<s.length())
            a[s.charAt(i)-'a']++;
            if(i<t.length())
            b[t.charAt(i)-'a']++;
        }

        for(int i=0;i<26;i++)
        {
            if(a[i]!=b[i])
                return false;
        }
        return true;
    }
}

