class Solution {
public:
    vector<string> removeAnagrams(vector<string>& words) {
        stack<string> s1;
        for(int i=0;i<words.size();i++){
            if(s1.empty()){
                s1.push(words[i]);
            }
            else{
                string s=s1.top(),s2=words[i];
                sort(s.begin(),s.end());
                sort(s2.begin(),s2.end());
                if(s==s2){
                    continue;
                }
                else{
                    s1.push(words[i]);
                }
            }
        }
        vector<string> ans;
        while(!s1.empty()){
            ans.push_back(s1.top());
            s1.pop();
        } 
        reverse(ans.begin(),ans.end());
        return ans;
    }
};
