        mp[words[0]]++;
        for(int i=1;i<words.size();i++){
            string temp = words[i];
            sort(words[i].begin(),words[i].end());
            mp[words[i]]++;
            if(mp[words[i]] == 1 || (words[i-1] != words[i])){
                res.push_back(temp);
            }
        }
        return res;
    }
};
