impl Solution {
    pub fn remove_anagrams(words: Vec<String>) -> Vec<String> {
        let maps = words
            .iter()
            .map(|w| {
                let mut lut = [0; 26];
                for c in w.chars() {
                    lut[c as usize - 'a' as usize] += 1;
                }
                lut
            })
            .collect::<Vec<[i32; 26]>>();

        let mut res = vec![];

        res.push(words[0].clone());
        for i in 1..words.len() {
            if maps[i] != maps[i - 1] {
                res.push(words[i].clone());
            }
        }
        res
    }
}
