public class Solution {
    private int[] GetCharMap(string s) {
        int[] map = new int[26];

        foreach (char c in s) {
            map[c - 'a']++;
        }

        return map;
    }

    private bool IsSameCharMap(int[] arr1, int[] arr2) {
        for (int i = 0; i < arr1.Length; i++) {
            if (arr1[i] != arr2[i]) {
                return false;
            }
        }

        return true;
    }

    public IList<string> RemoveAnagrams(string[] words) {
        List<string> result = new List<string> { words[0] };
        int[] charMap = GetCharMap(words[0]);

        for (int i = 1; i < words.Length; i++) {
            int[] nextCharMap = GetCharMap(words[i]);

            if (!IsSameCharMap(charMap, nextCharMap)) {
                charMap = nextCharMap;
                result.Add(words[i]);
            }
        }

        return result;
    }
}

