class Solution {
    public List<String> removeAnagrams(String[] words) {
        List<String> list = new ArrayList<>();
        int[] prev = new int[26];
        for(String word: words) {
            int[] curr = new int[26];
            for(char ch: word.toCharArray()) curr[ch - 'a']++;
            if(list.isEmpty()|| !Arrays.equals(curr, prev)) {
                list.add(word);
                prev = curr;
            }
        }
        return list;
    }
}
