class Solution {
public:
    vector<string> removeAnagrams(vector<string>& words) {
        for(int i=1;i<words.size();i++)
        {
            string frst=words[i];
            sort(frst.begin(),frst.end());
            string scnd=words[i-1];
            sort(scnd.begin(),scnd.end());
            if(frst==scnd)
            {
                words.erase(words.begin()+i);
                i--;
            }

        }
        return words;
    }
};
