class Solution:
    def removeAnagrams(self, words: List[str]) -> List[str]:
**        #mark elements to be removed**
        mark_idx = [0] * len(words)

**        #itr through the list, mark**
        for i in range(len(words) - 1):
            my_array = [0] * 26
            for j in range(len(words[i])):
                my_array[ord(words[i][j]) - 97] += 1
            comp_array = [0] * 26
            for l in range(len(words[i+1])):
                comp_array[ord(words[i+1][l]) - 97] += 1
            if all(elt1 == elt2 for elt1, elt2 in zip(my_array, comp_array)):
                mark_idx[i+1] = 1

**        #list to return**
        return_array = []
        for i in range(len(words)):
            if (mark_idx[i] == 0):
                return_array.append(words[i])
        return return_array
