class Solution {
public:
    vector<string> removeAnagrams(vector<string>& words) {
        int n=words.size();
        vector<string> ans;
        // ans.push_back(words[0]);
        int prev=0,i=1;
        if(n==1)
            return words;
        while(i<n)
        {
            while(i<n && isanagram(words[i],words[prev]))
            {
                i++;
            }
            ans.push_back(words[prev]);
            if(i==n-1)
                ans.push_back(words[i]);
            prev=i;
            i++;
        }
        return ans;
    }
   bool isanagram(string a,string b)
   {
       if(a.length()!=b.length())
        return false;
        sort(a.begin(),a.end());
        sort(b.begin(),b.end());
        if(a==b)
            return true;
        return false;
   }
};
