class Solution {
public:
    int maximumBags(vector<int>& capacity, vector<int>& rocks, int additionalRocks) {
        int n = rocks.size();
        vector<int>rem(n);
        for(int i = 0;i<n;i++){
            rem[i] = capacity[i]-rocks[i];
        }
        int ans = 0;
        sort(rem.begin(),rem.end());
        int i = 0;
        while(i<n && additionalRocks){
            int temp = min(rem[i],additionalRocks);
            rem[i]-=temp;
            additionalRocks-=temp;
            if(!rem[i]){
                ans++;
            }
            i++;
        }
        return ans;
    }
};
