class Solution {
public:
    int maximumBags(vector<int>& c, vector<int>& r, int aR) {
        int n = c.size();

        // Find out all the remaining capacities 
        vector<int> rc(n);
        for( int i=0 ; i<n ; i++ ){
            rc[i] = c[i]-r[i];
        }

        // Sort the remaining Capacities 
        // So that we end up filling max bags 
        sort( rc.begin(), rc.end() );

        for( int i=0 ; i<n ; i++ ){

            if( rc[i]>0 ){
                // if additional rocks are greater than capacity 
                // Update the additional rocks and remaining capacity becomes 0
                if( aR>rc[i] ){
                    aR = aR - rc[i];
                    rc[i] = 0 ;
                } 
                // if additional rocks are lesser than the capacity
                // Update the remaining capacity and additional rocks becomes 0
                else{
                    rc[i] -= aR ;
                    aR = 0;
                }
            }

            // if at any point additional rocks becomes 0 : Break
            if( aR == 0 ) break;
        }

        // Calculate the bags filled full 
        int ans=0;
        for( int i=0 ; i<n ; i++ ){
            if( rc[i] == 0 ) ans++;
        }

        return ans;
    }
};
```

