class Solution {
public:
    int maximumBags(vector<int>& c, vector<int>& r, int x) {
        vector<int>left;
        for(int i=0;i<c.size();i++)
            left.push_back(c[i]-r[i]);
        sort(left.begin(),left.end());
        int ans=0;
        for(int i=0;i<left.size();i++)
        {
            if(left[i]==0)
                ans++;
            else if(left[i]<=x)
            {
                ans++;
                x-=left[i];
            }
            if(x==0)
                break;
        }
        return ans;
    }
};
