impl Solution {
    pub fn maximum_bags(capacity: Vec<i32>, rocks: Vec<i32>, mut additional_rocks: i32) -> i32 {
        let mut need: Vec<i32> = (0..rocks.len())
            .map(|i| capacity[i] - rocks[i])
            .collect();
        need.sort_unstable();
        for i in 0..need.len() {
            if additional_rocks < need[i] {
                return i as i32;
            }
            additional_rocks -= need[i];
        }
        need.len() as i32
    }
}
