class Solution {
public:
    int maximumBags(vector<int>& capacity, vector<int>& rocks, int additionalRocks) {
        multiset<int> s;        // multiset to store the difference between capacity and rocks in increasing order.
        int res=0;  
        for(int i=0;i<capacity.size();i++)
            s.insert(capacity[i]-rocks[i]);         // storing  the difference in the multiset.
        for(auto it:s){
            if(it==0)
				res++;        // if no additional rocks are needed as the rocks are already at full capacity.
            else
            {
                if(additionalRocks>=it && additionalRocks>0){        //check if additional rocks are available or not.
                    res++;
                    additionalRocks-=it;
                }
            }

        }            
        return res;
    }
};
