class Solution {
public:
    int maximumBags(vector<int>& capacity, vector<int>& rocks, int k) {
        // result
        int ans = 0;     

        // using multiset  //  we can also use array but we need to sort it
		multiset<int> v1;
        int n = rocks.size();

        for(int i=0 ; i<n ; i++){
            int diff = capacity[i] - rocks[i];
            if(diff==0)     ans++;       // if zero max. capacity already
			else            v1.insert(diff);
        }

        for(auto &it : v1){
            if( it <= k  and  k > 0 ){           
                k -= it;
                ans++;
            }
        }
        return ans;
    }
};
