class Solution {
    public int maximumBags(int[] capacity, int[] rocks, int additionalRocks) {
        int length = capacity.length;
        int [] additionalPlaces = new int[length];

        for(int i=0;i<length;i++){
            additionalPlaces[i] = capacity[i]-rocks[i];
        }

        Arrays.sort(additionalPlaces);

        for(int i=0;i<length;i++){
            if(additionalPlaces[i]<=additionalRocks){
              additionalRocks-=additionalPlaces[i];
              additionalPlaces[i]=0;
            }
            else break;
        }

        for(int i=length-1;i>=0;i--){
            if(additionalPlaces[i]==0) return i+1;
        }

        return 0;
    }
}
