        vector<int> difference;
        for(int i = 0; i < l; i++)
            //store difference for each index
            difference.push_back(capacity[i] - rocks[i]);

        //sort the difference vector/array
        sort(difference.begin(), difference.end());

        //set maxPossible(bags) to 0
        int maxPossible = 0;
        for(int element: difference){
            //if AddRocks goes below 0 or to 0 terminate loop
            if(additionalRocks <= 0) break;
            //update AddRocks and maxPoss in each iteration
            additionalRocks -= element;
            maxPossible++;
        } 

        //addRocks = 0 means we filled all possible bags
        //addRocks > 0 means we filled all the bags to max Capacity and were still left with additional Rocks
        //addRocks < 0 means we we were in the process of filling the bags but we encountered a bag which required more rocks than the remaining rocks with us, we increased the maxBags in that iteration but were never able to succeffuly fill that bag completely, so we must decrease the count for that bag when returning the answer
        return additionalRocks >= 0 ? maxPossible : maxPossible - 1;

    }
};
```

