                // after 2nd execution of for loop for j res = k+0+1+1 = k2
                // after execution of for loop for i res = k+1+1+0 = L1
                res+=(c1+i);
                res+=(r1+j);
                ans.push_back(res);
            }
        }
        return ans;
    }
};
