class Solution {
public:
    vector<string> cellsInRange(string s) {
        vector<string> ans;
        char c1 = s[0];
        char r1 = s[1];
        char c2 = s[3];
        char r2 = s[4];

        char rmin = min(r1,r2);
        char rmax = max(r1,r2);
        char cmin = min(c1,c2);
        char cmax = max(c1,c2);

        while(cmin <= cmax){
            char l = rmin; 
