class Solution {
public:
    int convertToMinutes(string current){
        int minutes = 0;
        //add hrs after converting to minutes 02:35 ->02*60 = 120
        int i;
        string hrs = "";
        for(i=0; i<=1; i++){
             hrs += current[i];
        }
        int hrI = stoi(hrs);   //"02"  -> 02
        minutes += (hrI*60);   //02*60 -> 120

        string minute;
        for(i=3; i<=4; i++){
            minute += current[i];
        }
        int minuteI = stoi(minute);
        minutes += minuteI;

        return minutes;
    }

    int convertTime(string current, string correct) {
        int dest = convertToMinutes(current);
        int target = convertToMinutes(correct);

        int count = 0;
        while(dest != target){
            if(dest+60 <= target)
                dest += 60;
            else if(dest+15 <= target)
                dest += 15;
            else if(dest+5 <= target)
                dest += 5;
            else
                dest += 1;
            count++;
        }

        return count;
    }
};
