class Solution {
public:
    int convertTime(string current, string correct) {
        int res=0;
        string ans=current.substr(0,2);
        string ans1=current.substr(3,5);
        int a1=stoi(ans)*60;
        int a2=stoi(ans1);
         string st=correct.substr(0,2);
        string st1=correct.substr(3,5);
        int b1=stoi(st)*60;
        int b2=stoi(st1);

        int ff=abs((a1+a2)-(b1+b2));
        int hours = ff/60;
        res+=hours;
        int minutes=ff%60;
        if(minutes % 15 ==0 ){
            res+=minutes/15;
            return res;
        }
        if(minutes > 15 ){
            res+=minutes/15;
            minutes = minutes%15;
        }

        if(minutes%5==0){
            res+=minutes/5;
            return res;
