        def minutes(h: str, m: str) -> int:
            return int(h) * 60 + int(m)

        diff = abs(minutes(*current.split(":")) - minutes(*correct.split(":")))
        result = 0
        for divider in [60, 15, 5, 1]:
            ops, diff = divmod(diff, divider)
            result += ops
        return result
