class Solution {
    public int convertTime(String current, String correct) {
        int diff=0;
        int count=0;
        for(int i=0;i<current.length()-1;i++){
            if(current.charAt(i)==':' || current.charAt(i+1)==':') continue;
            if(count==0){
                int s1=current.charAt(i)-'0';
                int s2=correct.charAt(i)-'0';
                int s3=current.charAt(i+1)-'0';
                int s4=correct.charAt(i+1)-'0';
                s1=s1*10+s3;
                s2=s2*10+s4;  
                diff+=Math.abs(s1-s2)*60;

            }
            else{
                int s1=current.charAt(i)-'0';
                int s2=correct.charAt(i)-'0';
                int s3=current.charAt(i+1)-'0';
                int s4=correct.charAt(i+1)-'0';
                s1=s1*10+s3;
                s2=s2*10+s4;
                if(s1<s2){
                     diff+=Math.abs(s1-s2);
                }
                else{
                     diff-=Math.abs(s1-s2);
                }
            }
            count++;

        }
        int[] dp=new int[diff+1];
        Arrays.fill(dp,-1);
        return helper(diff,dp);
    }
    public int helper(int diff,int[] dp){
        if(diff<0){
            return (int)1e9;
        }
        if(dp[diff]!=-1){
            return dp[diff];
        }
        if(diff==0){
            return dp[diff]=0;
        }
        return dp[diff]=Math.min(1+helper(diff-1,dp),Math.min(1+helper(diff-5,dp),Math.min(1+helper(diff-15,dp),1+helper(diff-60,dp))));
    }
}

