class Solution {
public:
    bool vis[1500];
    int convertStringTimeToInt(string timeString){
        int pos = timeString.find(":");
        int hours = stoi(timeString.substr(0,pos));
        int mins = stoi(timeString.substr(pos+1,timeString.size()));

        mins = hours*60 + mins;

        return mins;
    }


    int convertTime(string current, string correct) {
          vector<int> operations = {1,5,15,60};
         int ct = convertStringTimeToInt(current);
         int cti = convertStringTimeToInt(correct);

         queue<int> q;
        q.push(cti);

        int level=0;

        while(q.size()){

            int s=q.size();
            while(s--){
                int c=q.front();
                q.pop();

                if(c==ct) return level;

                if(vis[c]==true || c<0) continue;

                vis[c]=true;

                for(int i=0;i<operations.size();i++)
                      if(c-operations[i]>=0)
                      q.push(c-operations[i]);

               }
            level++;

        }
        return level;
    }
};
