var convertTime = function (current, correct) {
    let currentArr = current.split(":")
    let currentMins = Number(currentArr[0]) * 60 + Number(currentArr[1]) 

    let correctArr = correct.split(":")
    let correctMins = Number(correctArr[0]) * 60 + Number(correctArr[1])

    let noOfOperations = 0
    let deltaTime = correctMins - currentMins

    while (deltaTime > 0) {
        if (deltaTime >= 60) currentMins += 60
        else if (deltaTime >= 15) currentMins += 15
        else if (deltaTime >= 5) currentMins += 5
        else if (deltaTime >= 1) currentMins += 1

        deltaTime = correctMins - currentMins
        noOfOperations++
    };
    return noOfOperations
}
