var convertTime = function(current, correct) {
    var currentSplit = current.split(":");
    var correctSplit = correct.split(":");
    var currentMins = parseInt(currentSplit[0]) * 60 + parseInt(currentSplit[1]);
    var correctMins = parseInt(correctSplit[0]) * 60 + parseInt(correctSplit[1]);
    var differenceMins = (correctMins - currentMins);

    // 60 Minutes
    var count60 = Math.floor(differenceMins / 60);
    differenceMins -= count60 * 60;

    // 15 Minutes
    var count15 = Math.floor(differenceMins / 15);
    differenceMins -= count15 * 15;

    // 5 Minutes
    var count5 = Math.floor(differenceMins / 5);
    differenceMins -= count5 * 5;

    // 1 Minutes
    var count1 = Math.floor(differenceMins);

    return count60 + count15 + count5 + count1;
};
