func convertTime(current string, correct string) int {
	if current == correct {
		return 0
	}
	hour, _ := strconv.Atoi(current[:2])
	thour, _ := strconv.Atoi(correct[:2])
	minute, _ := strconv.Atoi(current[3:])
	tminute, _ := strconv.Atoi(correct[3:])
	target := thour*60 + tminute
	from := hour*60 + minute
	return timeTakes(target-from, 0)

}

func timeTakes(minute int, operation int) int {

	if minute == 0 {
		return operation
	}

	if minute >= 60 {
		return timeTakes(minute%60, operation+(minute/60))
	} else if minute >= 15 {
		return timeTakes(minute%15, operation+(minute/15))
	} else if minute >= 5 {
		return timeTakes(minute%5, operation+(minute/5))
	} else {
		return timeTakes(minute%1, operation+(minute/1))
	}
}
