        if(cur+15 <= cor)
            return helper(cur+15, cor, ans+1);

        if(cur+5 <= cor)
            return helper(cur+5, cor, ans+1);

        if(cur+1 <= cor)
            return helper(cur+1, cor, ans+1);

        return 0;
    }
}
