class Solution {
    public int convertTime(String current, String correct) {
        // current is formatted as HH:MM hour and minute

        // Increase current by 1, 5, 15, 60 minutes
        // Split the string, turn it all into minutes
        // Loop for 60 -> 15 -> 5 -> 1 until 0
        // Return number of operations needed to get to correct

        // CURRNENT
        int hr = Integer.valueOf(current.substring(0, 2))*60;
        int min = Integer.valueOf(current.substring(3));
        int total = hr+min;
        int[] times = {60, 15, 5, 1};
        int count = 0;

        // CORRECT
        int hrC = Integer.valueOf(correct.substring(0, 2))*60;
        int minC = Integer.valueOf(correct.substring(3));
