/**
 * @param {string} current
 * @param {string} correct
 * @return {number}
 */
var convertTime = function(current, correct) {
    var count = 0;

    var crtArr = current.split(':')
    var crcArr = correct.split(':')

    var crtHH = Number(crtArr[0])
    var crcHH = Number(crcArr[0])
    var diffHH = crcHH - crtHH;
    count = count + diffHH;

    var crtMM = Number(crtArr[1])
    var crcMM = Number(crcArr[1])

    // Detect if Minutes Current > Minutes Correct
    if(crtMM <= crcMM){
        var diffMM = crcMM - crtMM
    }else{
        diffMM = crcMM + (60 - crtMM)
        count = count - 1
    }

    var d15 = diffMM/15
    var remain = diffMM%15
    if(d15>0){
        count = count + Math.floor(d15)
    }

    if(remain){
        var d5 = remain/5
        remain = remain%5
        if(d5>0){
             count = count + Math.floor(d5)
        }
    }
    if(remain){
        count = count + remain
    }

    return count
};
