class Solution {
public:

    long long funbin(vector<int> &v,vector<long long> &pre,long long f,int t)
    {

        if(v.size()==0 || v[0]>=t)
            return 0;
        int r = 0,l = t-1;
        while(r<=l)
        {
            long long mid = (r+l)/2;
            long long sum = 0;
            long long rr = 0,ll = v.size()-1;
            while(rr<=ll)
            {
                long long midd = (rr+ll)/2;
                if(v[midd]>=mid)
                    ll = midd-1;
                else
                    rr = midd+1;
            }

            if(ll>=0)
                sum = pre[ll];

            long long sum1 = 1ll*(ll+1)*(mid) - sum;

            if(sum1>f)
                l = mid-1;
            else
                r = mid+1;

        }

        return l;
    }
    long long maximumBeauty(vector<int>& v, long long f, int t, int fu, int pa){

        sort(v.begin(),v.end());
        int n = v.size();
        vector<long long> pre(n);
        pre[0] = v[0];

        for(int i=1; i<v.size(); ++i)
            pre[i]+=pre[i-1] + v[i];

        long long fans = pa*funbin(v,pre,f,t);
        int coun = 0;
        while((int)v.size())
        {
            coun++;
            int x = v.back();
            v.pop_back();
            long long a = max(0,t-x);
            f-=a;
            if(f<0)
                break;

            long long lo = funbin(v,pre,f,t);

            fans = max(fans,1ll*coun*fu + 1ll*pa*lo);  
        }


        return fans;
    }

};
