typedef long long ll;
class Solution {
public:
    long long maximumBeauty(vector<int>& flowers, long long newFlowers, int target, int full, int partial) {
        sort(flowers.begin(), flowers.end());
        ll baseline = 0;
        while (!flowers.empty() && flowers.back() >= target) {
            baseline += full;
            flowers.pop_back();
        }

        ll best = 0;
        ll water_level = 0;  // value of the minimum partial garden.
        ll water_width = 0;  // number of such gardens.
        deque<ll> pending;  // non-minimum partial gardens.

        // In each iteration, try to use the budget below to increase water_level.
        // Budget could be negative, indicating the scenario is invalid as we couldn't make that many gardens 'full'.
        ll budget = newFlowers;
        for (int x : flowers) {
            budget -= (target - x);
        }

        for (int num_partials = 0; num_partials <= flowers.size(); num_partials++) {
            if (budget >= 0) {
                ll full_sum = (ll)full * (flowers.size() - num_partials);
                ll partial_sum = water_level * partial;
                ll cur = full_sum + partial_sum;
                best = max(best, cur);
            }
            if (num_partials == flowers.size()) break;

            pending.push_back(flowers[num_partials]);
            budget += target - flowers[num_partials];

            while (budget > 0) {
                ll next_water_level = min<ll>(pending.empty() ? target - 1 : pending.front(), water_width == 0 ? INT32_MAX : water_level + (budget / water_width));
                budget -= (next_water_level - water_level) * water_width;
                water_level = next_water_level;
                bool consumed = false;
                while (!pending.empty() && pending.front() == water_level) {
                    water_width++;
                    pending.pop_front();
                    consumed = true;
                }
                if (water_level == target - 1 || !consumed) break;
            }
        }
        return best + baseline;

    }
};

