class Solution {
public:
    long long maximumBeauty(vector<int>& flowers, long long newFlowers, int target, long long full, long long partial)  
    {
        sort(flowers.rbegin(), flowers.rend());
        long long l = 0, n=flowers.size();;
        while(l < n && flowers[l] >= target) l++;
        long long ans = 0;
        vector<long long> required(n, 0);
        for(long long i = n-2 ; i >= l ; i--){
            required[i] = required[i+1] + (n-i-1)*(flowers[i]-flowers[i+1]);
        }

        long long pre = l*full;
        ans = pre;
        for(int i = l ; i < n ; i++){
            long long part = solve(flowers, required, newFlowers, i, target);
            ans = max(ans, pre+part*partial);
            if(target-flowers[i] <= newFlowers){
                pre += full;
                newFlowers -= (target-flowers[i]);
            }else{
                break;
            }
        }
        return ans = max(ans, pre);
    }

    long long solve(vector<int>& flowers, vector<long long>& req, long long newFlowers, int ind, long long target){
        long long l = ind, r = flowers.size(), n = flowers.size();
        long long ans = 0;
        while(l <= r){
            int mid = (l+r)/2;
            long long cnt = req[mid];
            if(cnt > newFlowers){
                l = mid+1;
            }else{
                ans = flowers[mid] + (newFlowers-cnt)/(n-mid);
                r = mid-1;
            }
        }
        return min(ans, target-1);
    }
};
