class Solution {
public:
    long long maximumBeauty(vector<int>& flowers, long long newFlowers, int target, int full, int partial) {
        sort(flowers.begin(), flowers.end());
        int n = flowers.size();
        if (flowers[0] >= target) return n * (long long)full;
        vector<long long> fillLeft(n, 0);
        for (int i = 1; i < n; ++i) {
            fillLeft[i] = fillLeft[i - 1] + i * (long long)(flowers[i] - flowers[i - 1]);
        }

        long long ret = 0;
        int r = n, l = n - 1;
        while (r > -1 && newFlowers > 0 && l > -1) {
            if (r < n) {
                if (flowers[r] < target) newFlowers += (flowers[r] - target);
                if (newFlowers <= 0) return ret;
            }
            long long val = 0;
            if (r > 0) {
                l = min(r - 1 , l);
                while (l > -1 && fillLeft[l] > newFlowers) --l; 
                ++l;
                if (l == 0) val = flowers[0] + newFlowers;
                else  val = flowers[l - 1] + (newFlowers - fillLeft[l - 1])/l;
                val = min(val, (long long)target -1);
            } 
            ret = max(ret, full * (long long) (n - r) + val * partial);
            --r;
        }
        return ret;
    }
};

