typedef long long ll;

class Solution {
public:

    ll getMaxMin(vector<int>& arr, int i, int n, ll cl, ll &s, vector<ll>& suf){

        // Here we are finding the maximum min val that can be obtained using 's'. Since values cannot be more than target,
        // res will have an upper bound of cl.

        if(i == n) return 0;
        ll res = arr[n-1];
        ll l = res, r = cl;

        while(l <= r){
            // Here we use suffix sum to find if mid can be achieved using 's' values. For e.g for arr{4,3,2,1} to have a min value 
            // of 4, we calculate upper bound on 4, which is index 1(i.e. value 3). Now if 4 were to be the min value in the array,
            // then sum[1...3] should be 3*4 = 12. Actual sum is suf[1], i.e. 6. Now we compare this value against s.
            ll mid = (l+r) >> 1;
            int k = upper_bound(arr.begin()+i, arr.end(), mid, greater<int>())-arr.begin();
            if(k == n) k--;

            ll req = (((ll)(n-k) * (mid))-suf[k]);

            if(req > s){
                r = mid-1;
            }else{
                res = mid;
                l = mid+1;
            }
        }

        return res;
    }

    long long maximumBeauty(vector<int>& flowers, long long newFlowers, int target, int full, int partial) {
        //use a greedy approach, start with 0 full gardens, then 1 full garden upto n;
        sort(flowers.begin(), flowers.end(), [](const int& a, const int& b) -> bool{
            return a > b;
        });
        int n = flowers.size();
        int l = 0;
        //skip already full gardens
        while(l < n && flowers[l] >= target) l++;

        if(l == n) return (l*(ll)full);

        ll cl = target-1;

        //Find suffix sum
        vector<ll> suf(n);
        suf[n-1] = flowers[n-1];
        for(int i = n-2 ; i >=0 ; i--)
            suf[i] += (suf[i+1] + flowers[i]);

        ll fullCnt = l;

        ll res = (fullCnt * full + partial*getMaxMin(flowers, l, n, target-1, newFlowers, suf));

        for(int i = l ; i < n ; i++){
            ll reqFlowers = target-flowers[i];
            if(reqFlowers > newFlowers) break;
            newFlowers -= reqFlowers;
            fullCnt = i+1;

            res = max(res, fullCnt*full + partial*getMaxMin(flowers, i+1, n, target-1, newFlowers, suf));
        }

        return res;

    }
};

