class Solution {
public:
    long long maximumBeauty(vector<int>&nums, long long nf, long long target, long long f, long long partial) {
//      sorting the array
        sort(nums.begin(),nums.end());
        long long full=0;
//      count the already full gardens 
        for(auto num:nums){
            full+=(num>=target);
        }
//       create a prefix array
        vector<long long>pre(nums.size()-full);
        for(long long i=1ll;i<nums.size()-full;i++){
            pre[i]=pre[i-1]+(nums[i]-nums[i-1])*1ll*i;
        }


//      declare answer  
        long long ans=0;
//      if all gardens are alreay full, we can't proceed further
        if(full==nums.size())return full*1ll*f;
        ans=max(ans,full*f+nums[0]*partial);

//      increasing the size of nums by one to handle the case of (already full == 0);
        nums.push_back(target);

//      loop from back
        for(int i = pre.size() ; i>=0;i--){
//      handle the case i==0 separately because prefix array will be empty this time and we cannot apply upper_bound on empthy vector
            if(i==0){
                if(nf>=target*1ll-nums[i]*1ll){
                    full++;
                    ans=max(ans,f*1ll*full) ;
                }
            }
//          if we are able to make ith garden full
            else if(nf>=target-nums[i]){
//              decreasing the flowers available  
                nf-=max(target-nums[i],0ll);
//              increasing count of full gardens because our current garden is also full now 
                if(nums[i]<target)full++;

//              getting the index off the garden whose pre[index] <= available flowers;
                long long ind = upper_bound(pre.begin(),pre.end(),nf)-pre.begin()-1;
//              how much extra flowers we can add to the gardens to increase minimum
                long long delta=(nf*1ll-pre[ind])/(ind+1);

//              updating the answer
                if(delta + nums[ind] >= target)delta=target-nums[ind]-1;
                ans=max(ans , full *(1ll)* f + partial*1ll*(nums[ind]+delta));
//             decreasing the size of pre
                pre.pop_back();

            }else break;
        }
        return ans;        
    }
};
