class Solution {
public:
    long long maximumBeauty(vector<int>& flowers, long long newFlowers, int target, int full, int partial) {
        int n  = flowers.size();
        sort(flowers.begin(), flowers.end(), greater<int>());
        vector<long long> prefix(n, LONG_LONG_MAX);
        prefix[n-1] = 0;
        for(int i = n-2; i >= 0; i--)
        {
            if(flowers[i] < target)
                    prefix[i] = (flowers[i] - flowers[i+1])*(n-i-1) + prefix[i+1];
            else
            {
                prefix[i] = (target-1 - flowers[i+1])*(n-i-1) + prefix[i+1];
                break;
            }
        }
        int start = 0;
        while(start < n && flowers[start] >= target)
            start++;
        long long comp = start;
        long long ans = (long long)start*full;
        for(int i = start; i < n; i++)
        {
            int index = lower_bound(prefix.begin()+i, prefix.end(), newFlowers, greater<long long>()) - prefix.begin();
            long long rem = newFlowers - prefix[index];
            long long min_value = min(flowers[index] + rem/(n-index), (long long)target-1);
            ans = max(ans, min_value*partial + comp*full);
            if(newFlowers - target+flowers[i] >= 0)
            {
                newFlowers= newFlowers-(target-flowers[i]);
                comp++;
            }
            else
                break;
            if(newFlowers <= 0)
                break;
        }
        if(newFlowers >= target - flowers[n-1])
            ans = max((long long)n*full, ans);
        return ans;

    }
};
